#include<iostream>using namespace std;void initInitialize();char getSign(int i,int j);void printBoard();int checkWinningState();int board[3][3];int main(){	int winningState = -1;	initInitialize();	cout<<"\n\t TicTacToe"<<endl;	cout<<"............................"<<endl;	for(int i=1;i<=9;i++){		printBoard();		if(i%2==1){			cout<<"\n1st Player: ";			int r,c;			cin>>r>>c;			if(r<=3&&r>=1&&c<=3&&c>=1&&board[r-1][c-1]==-1){				board[r-1][c-1] = 0;			}else{				cout<<"\nInvalid Input"<<endl;				i--;			}		}else{			cout<<"\n2nd Player: ";			int r,c;			cin>>r>>c;			if(r<=3&&r>=1&&c<=3&&c>=1&&board[r-1][c-1]==-1){				board[r-1][c-1] = 1;			}else{				cout<<"\nInvalid Input"<<endl;				i--;			}		}		winningState = checkWinningState();		if(winningState!=(-1)) break;	}	printBoard();	cout<<"\n......................................."<<endl;	cout<<"\n\t\tGAME OVER"<<endl;	cout<<"\n......................................."<<endl;	if(winningState==0) cout<<"\n\t 1st PLAYER IS THE WINNER"<<endl;	else if(winningState==1) cout<<"\n\t 2nd PLAYER IS THE WINNER"<<endl;	else cout<<"\n\t NO ONE IS THE WINNER"<<endl;	cout<<"\n......................................."<<endl;	cout<<endl;	return 0;}int checkWinningState(){	if(board[0][0]==board[0][1]&&board[0][0]==board[0][2]&&board[0][0]!=-1) return board[0][0];	else if(board[1][0]==board[1][1]&&board[1][0]==board[1][2]&&board[1][0]!=-1) return board[1][0];	else if(board[2][0]==board[2][1]&&board[2][0]==board[2][2]&&board[2][0]!=-1) return board[2][0];	else if(board[0][0]==board[1][0]&&board[0][0]==board[2][0]&&board[0][0]!=-1) return board[0][0];	else if(board[0][1]==board[1][1]&&board[0][1]==board[2][1]&&board[0][1]!=-1) return board[0][1];	else if(board[0][2]==board[1][2]&&board[0][2]==board[2][2]&&board[0][2]!=-1) return board[0][2];	else if(board[0][0]==board[1][1]&&board[0][0]==board[2][2]&&board[0][0]!=-1) return board[0][0];	else if(board[0][2]==board[1][1]&&board[0][2]==board[2][0]&&board[0][2]!=-1) return board[0][2];	else return -1;}void printBoard(){	cout<<endl;	cout<<"\t "<<getSign(0,0)<<" | "<<getSign(0,1)<<" | "<<getSign(0,2)<<endl;	cout<<"\t..........."<<endl;	cout<<"\t "<<getSign(1,0)<<" | "<<getSign(1,1)<<" | "<<getSign(1,2)<<endl;	cout<<"\t..........."<<endl;	cout<<"\t "<<getSign(2,0)<<" | "<<getSign(2,1)<<" | "<<getSign(2,2)<<endl;}char getSign(int i,int j){	if(board[i][j]==-1) return ' ';	else if(board[i][j]==0) return 'O';	else return 'X';}void initInitialize(){	for(int i=0;i<3;i++)		for(int j=0;j<3;j++)			board[i][j] = -1;}